				  //数量增加
				  $(document).on('click','.tb-increase',function(){
					//获取库存
					var count = $('#J_SpanStock').html();
					var num = $('.tb-stock').find('input').val();
					var num = parseInt(num)+1;
					$('.tb-stock').find('input').val(num);
					$('.tb-reduce').removeClass('tb-disable-reduce');
					if(num >= count){
						$(this).addClass('tb-disable-reduce');
						$('.tb-stock').find('input').val(count);
					}
				  });
				  //数量减少
				  $(document).on('click','.tb-reduce',function(){
					//获取库存
					var num = $('.tb-stock').find('input').val();
					if(num > 1){
						num = parseInt(num)-1;
						$('.tb-stock').find('input').val(num);
						$('.tb-increase').removeClass('tb-disable-reduce');
					}else{
						$(this).addClass('tb-disable-reduce');
					}
				  });
